pub fn blitAttrSlot(attr_name: []const u8) ?usize {
    if (std.mem.eql(u8, attr_name, "position")) {
        return 0;
    }
    if (std.mem.eql(u8, attr_name, "color0")) {
        return 1;
    }
    return null;
}
pub fn blitImageSlot(img_name: []const u8) ?usize {
    if (std.mem.eql(u8, img_name, "tex")) {
        return 0;
    }
    return null;
}
pub fn blitSamplerSlot(smp_name: []const u8) ?usize {
    if (std.mem.eql(u8, smp_name, "tex_smp")) {
        return 0;
    }
    return null;
}
pub fn blitUniformblockSlot(ub_name: []const u8) ?usize {
    if (std.mem.eql(u8, ub_name, "vs_params")) {
        return 0;
    }
    if (std.mem.eql(u8, ub_name, "fs_params")) {
        return 1;
    }
    return null;
}
pub fn blitUniformblockSize(ub_name: []const u8) ?usize {
    if (std.mem.eql(u8, ub_name, "vs_params")) {
        return @sizeOf(VsParams);
    }
    if (std.mem.eql(u8, ub_name, "fs_params")) {
        return @sizeOf(FsParams);
    }
    return null;
}
pub fn blitUniformOffset(ub_name: []const u8, u_name: []const u8) ?usize {
    if (std.mem.eql(u8, ub_name, "vs_params")) {
        if (std.mem.eql(u8, u_name, "yolo")) {
            return 0;
        }
    }
    if (std.mem.eql(u8, ub_name, "fs_params")) {
        if (std.mem.eql(u8, u_name, "yolo")) {
            return 0;
        }
    }
